import React from 'react';
import './Grid.css';
const keys = [
  { top: 'a', bottom: 'b' },
  { top: 'c', bottom: 'd' },
  { top: 'e', bottom: 'f' },
  { top: 'g', bottom: 'h' },
  { top: 'i', bottom: 'j' },
  { top: 'l', bottom: 'k' },
  { top: 'n', bottom: 'm' },
  { top: 'o', bottom: 'p' },
  { top: 'r', bottom: 'q' },
  { top: 's', bottom: 't' },
  { top: 'u', bottom: 'v w' },
  { top: 'x', bottom: 'y z' },
  { top: '⇦', type: 'function', action: 'delete' },
  { top: '⇧', type: 'function', action: 'caps' },
  { top: '␣', type: 'function', action: 'space' },
  { top: '123', bottom: '#+=', type: 'function', action: 'link' },
];
const Grid = ({ onKeyTap }) => {
  //   const renderCells = () => {
  //     let cells = [];
  //     for (let i = 0; i < 16; i++) {
  //       cells.push(<div key={i} className='cell' />);
  //     }
  //     return cells;

  //   };
  const renderCells = () => {
    return keys.map((key, i) => (
      <div
        key={i}
        className={`cell ${key.type || ''}`}
        onClick={() => onKeyTap(key)}
      >
        <div className='top'>{key.top}</div>
        {key.bottom && <div className='bottom'>{key.bottom}</div>}
      </div>
    ));
  };
  return <div className='grid'>{renderCells()}</div>;
};

export default Grid;
