import React, { useState } from 'react';
import Grid from './Grid';
import './App.css'; // Import the CSS for styling

const App = () => {
  const [inputValue, setInputValue] = useState('');
  const [lastTap, setLastTap] = useState(null);
  const [tapTimeoutId, setTapTimeoutId] = useState(null);
  const [tapCount, setTapCount] = useState(0); // Track the number of taps

  const tapDelay = 1000;

  const handleKeyTap = (key) => {
    const currentTime = new Date().getTime();
    // console.log(currentTime, lastTap);
    const isQuickTap = lastTap && currentTime - lastTap < tapDelay;
    let newTapCount = isQuickTap ? tapCount + 1 : 1;

    const handleSingleTap = () => {
      if (key.type === 'function') {
        switch (key.action) {
          case 'delete':
            setInputValue((prevValue) => prevValue.slice(0, -1));
            break;
          case 'caps':
            // Logic for caps lock goes here
            break;
          case 'space':
            setInputValue((prevValue) => prevValue + ' ');
            break;
          case 'link':
            // Logic to change keyboard or render new screen goes here
            break;
          default:
            break;
        }
      } else {
        setInputValue((prevValue) => prevValue + key.top);
      }
    };
    const handleDoubleTap = () => {
      if (key.bottom) {
        if (key.type === 'function') {
          if (key.action === 'link') {
            // change keyboard, render new screen
          }
        } else {
          setInputValue((prevValue) => prevValue + key.bottom.split(' ')[0]);
        }
      }
    };

    const handleTripleTap = () => {
      if (key.bottom && key.bottom.includes(' ')) {
        setInputValue((prevValue) => prevValue + key.bottom.split(' ')[1]);
      }
    };

    clearTimeout(tapTimeoutId);
    const timeoutId = setTimeout(() => {
      if (newTapCount === 1) handleSingleTap();
      else if (newTapCount === 2) handleDoubleTap();
      else if (newTapCount === 3) handleTripleTap();

      setTapCount(0);
    }, tapDelay);

    setTapTimeoutId(timeoutId);
    setTapCount(newTapCount);
    setLastTap(currentTime);
  };
  return (
    <div className='container'>
      <div>
        <h1 className='title'>TinyType</h1>
      </div>
      <div className='inputContainer'>
        <textarea
          className='textInput'
          placeholder='Type here...'
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
      </div>
      <div className='gridContainer'>
        <Grid onKeyTap={handleKeyTap} />
      </div>
      <div>
        <h3 className='rulesHeading'>Rules â†“</h3>
        <h6 className='rules'>
          Top letter: Single Tap | Bottom/Bottom Left: Double Tap
        </h6>
        <h6 className='rules'>
          Bottom Right: Triple Tap | Hold for character repeat
        </h6>
      </div>
    </div>
  );
};

export default App;
