import React, { useState } from 'react';
import Grid from './Grid';
import './App.css'; // Import the CSS for styling

const App = () => {
  const [inputValue, setInputValue] = useState('');
  const [lastTap, setLastTap] = useState(0);
  const [isDoubleTap, setIsDoubleTap] = useState(false);

  const handleKeyTap = (key) => {
    // Here you would handle the logic to update the inputValue based on the key tapped
    const currentTime = new Date().getTime();
    const tapDelay = 4000;
    console.log(currentTime, lastTap);
    if (currentTime - lastTap < tapDelay) {
      // If this tap happened soon after the last one, it's a double tap
      setIsDoubleTap(true);
    } else {
      // If not, reset the double tap state after a delay to allow for a potential second tap
      setTimeout(() => setIsDoubleTap(false), tapDelay);
    }
    setLastTap(currentTime);

    if (key.type === 'function') {
      // Implement functionality for special keys like DELETE, CAPS, etc.
      switch (key.action) {
        case 'delete':
          setInputValue((prevValue) => prevValue.slice(0, -1));
          break;
        case 'caps':
          // You would need additional logic to handle caps lock state
          break;
        case 'space':
          setInputValue((prevValue) => prevValue + ' ');
          break;
        case 'numbers':
          // You would need additional logic to switch to a numbers/symbols layout
          break;
        default:
          break;
      }
    }

    setTimeout(() => {
      if (isDoubleTap && key.bottom) {
        // If it's a double tap and the key has a bottom character, add the bottom character
        setInputValue((prevValue) => prevValue + key.bottom.split(' ')[0]); // Take the first character if there are two
        setIsDoubleTap(false); // Reset the double tap state
      } else if (key.top) {
        // If it's a single tap or a functional key, add the top character
        setInputValue((prevValue) => prevValue + key.top);
      }
    }, tapDelay);
  };
  return (
    <div className='container'>
      <div>
        <h1 className='title'>TinyType</h1>
      </div>
      <div className='inputContainer'>
        <textarea
          className='textInput'
          placeholder='Type here...'
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
      </div>
      <div className='gridContainer'>
        <Grid onKeyTap={handleKeyTap} />
      </div>
      <div>
        <h3 className='rulesHeading'>Rules â†“</h3>
        <h6 className='rules'>
          Top letter: Single Tap | Bottom/Bottom Left: Double Tap
        </h6>
        <h6 className='rules'>
          Bottom Right: Triple Tap | Hold for character repeat
        </h6>
      </div>
    </div>
  );
};

export default App;
