import React, { useState } from 'react';
import Grid from './Grid';
import './App.css'; // Import the CSS for styling

const App = () => {
  const [inputValue, setInputValue] = useState('');
  const [lastTap, setLastTap] = useState(0);

  const handleKeyTap = (key) => {
    // Here you would handle the logic to update the inputValue based on the key tapped
    const currentTime = new Date().getTime();
    const tapDelay = 300;

    console.log(currentTime, lastTap);
    const handleSingleTap = () => {
      if (key.type === 'function') {
        // Handle function keys like delete here
        if (key.action === 'delete') {
          setInputValue((prevValue) => prevValue.slice(0, -1)); // Remove the last character
        }
        if (key.action === 'caps') {
          // You would need additional logic to handle caps lock state
        }
        if (key.action === 'space') {
          setInputValue((prevValue) => prevValue + ' ');
        }
        if (key.action === 'link') {
          // change keyboard, render new screen
        }
        // Add other functional key handlers here...
      } else {
        // Add the top character for regular keys
        setInputValue((prevValue) => prevValue + key.top);
      }
    };

    const handleDoubleTap = () => {
      if (key.type === 'function') {
        if (key.action === 'link') {
          // change keyboard, render new screen
        }
      } else {
        if (key.bottom) {
          setInputValue((prevValue) => prevValue + key.bottom.split(' ')[0]); // Take the first character if there are two
        }
      }
    };

    // const handleTripleTap = () => {
    //   if (key.bottom && length(key.bottom) === 3) {
    //     setInputValue((prevValue) => prevValue + key.bottom.split(' ')[1]);
    //   }
    // };
    if (currentTime - lastTap < tapDelay) {
      // If this tap happened soon after the last one, it's a double tap
      setLastTap(0); // Reset the last tap time
      handleDoubleTap(); // Handle double tap
    } else {
      // If not, process as a single tap after a delay, allowing for a potential second tap
      setTimeout(() => {
        if (currentTime - lastTap >= tapDelay) {
          // Check if no additional taps occurred
          handleSingleTap();
        }
      }, tapDelay);
      setLastTap(currentTime); // Record the time of this tap
    }
  };
  return (
    <div className='container'>
      <div>
        <h1 className='title'>TinyType</h1>
      </div>
      <div className='inputContainer'>
        <textarea
          className='textInput'
          placeholder='Type here...'
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
      </div>
      <div className='gridContainer'>
        <Grid onKeyTap={handleKeyTap} />
      </div>
      <div>
        <h3 className='rulesHeading'>Rules â†“</h3>
        <h6 className='rules'>
          Top letter: Single Tap | Bottom/Bottom Left: Double Tap
        </h6>
        <h6 className='rules'>
          Bottom Right: Triple Tap | Hold for character repeat
        </h6>
      </div>
    </div>
  );
};

export default App;
