import React from 'react';
import './Grid.css';
const letterkeys = [
  { top: 'a', bottom: 'b' },
  { top: 'c', bottom: 'd' },
  { top: 'e', bottom: 'f' },
  { top: 'g', bottom: 'h' },
  { top: 'i', bottom: 'j' },
  { top: 'l', bottom: 'k' },
  { top: 'n', bottom: 'm' },
  { top: 'o', bottom: 'p' },
  { top: 'r', bottom: 'q' },
  { top: 's', bottom: 't' },
  { top: 'u', bottom: 'v w' },
  { top: 'x', bottom: 'y z' },
  { top: '⌫', type: 'function', action: 'delete' },
  { top: '⇧', type: 'function', action: 'caps' },
  { top: '␣', type: 'function', action: 'space' },
  { top: '123', bottom: '#+=', type: 'function', action: 'num_punctuation' },
];

const numberKeys = [
  { top: '1', bottom: '!' },
  { top: '2', bottom: '@' },
  { top: '3', bottom: '#' },
  // Add other number keys...
  { top: 'ABC', type: 'function', action: 'letters' }, // Switch back to letters
];

const symbolKeys = [
  { top: '#', bottom: '*' },
  { top: '+', bottom: '-' },
  { top: '=', bottom: '/' },
  // Add other symbol keys...
  { top: 'ABC', type: 'function', action: 'letters' }, // Switch back to letters
];

const Grid = ({ onKeyTap, isCapsLock, keypadMode }) => {
  const keys =
    keypadMode === 'numbers'
      ? numberKeys
      : keypadMode === 'symbols'
      ? symbolKeys
      : letterKeys;
  const renderCells = () => {
    return keys.map((key, i) => {
      const isCapsKey = key.action === 'caps';
      return (
        <div
          key={i}
          className={`cell ${key.type || ''}`}
          onClick={() => onKeyTap(key)}
        >
          <div className='top'>
            {isCapsKey
              ? isCapsLock
                ? '⬆'
                : '⇧'
              : isCapsLock
              ? key.top.toUpperCase()
              : key.top}
          </div>
          {key.bottom && (
            <div className='bottom'>
              {isCapsLock ? key.bottom.toUpperCase() : key.bottom}
            </div>
          )}
        </div>
      );
    });
  };
  return <div className='grid'>{renderCells()}</div>;
};
export default Grid;
